import React, {useState, useEffect} from 'react'

import { TextInput } from '../../../components/TextInput/TextInput';
import './RegisterFormOne.css';


//props
interface FormOneState {
    firstName: string;
    lastName: string;
    email: string;
    dateOfBirth: string;
}

// components
export const RegisterFormOne: React.FC = () => {

    const [stepOneState, setStepOneState] = useState<FormOneState>({
        firstName: " ", 
        lastName: " ",
        email: " ",
        dateOfBirth: " ",
    }) 

    // step one uodate user
    const updateUser= (e:React.ChangeEvent<HTMLInputElement>): void => {
        setStepOneState({...stepOneState, [e.target.name]: e.target.value })
    }



    // console display the update function
    useEffect(() => {
        console.log("State change: ", stepOneState);
    })



  return (
    <div className='reg-step-one-container'>
        <div className="reg-step-one-content">
            <TextInput name={'firstName'}  label = {"First"} errorMessage= {"Please enter your First name"}  onChange={updateUser} />
            <TextInput name={'lastName'}  label = {"Last"} errorMessage= {"Please enter your Lastname name "}  onChange={updateUser} />
            <TextInput name={'email'}  label = {"email"} errorMessage= {"Please enter a valied email"}  onChange={updateUser} />
        </div>
    </div>
  )
}
