import React, {useState} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { AppDispatch, RootState } from '../../../../redux/Store'

import { Modal } from '../../../../components/Modal/ModalOld'
import "./RegisterModal.css"
import { RegistrationStepCounter } from '../RegisterStepCounter/RegisterationStepCounter'
import { determineModalContent } from '../../util/RegisterModalUtils'
import { decrementStep } from '../../../../redux/Slices/RegisterSlice'



export const RegisterModal:React.FC = () => {

  const state = useSelector((state:RootState) => state.reggister)
  const dispatch:AppDispatch = useDispatch();

  //function to change the step number using the current variable and the update variables in the hooks
  const stepButtonClicked =  () => {
      dispatch(decrementStep());
  }

  return ( 
        <Modal>
            <div className='register-container'>
                <RegistrationStepCounter step={state.step} changeStep={stepButtonClicked}/>
                <div className="register-modal-content"> 
                {determineModalContent(state.step)}
                </div>
            </div>
        </Modal>
  )
}
