import React, {useEffect, useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { AppDispatch, RootState } from "../../../../redux/Store";

import { CheckBox } from "../../../../components/Checkbox/CheckBox";
import { DropDown } from "../../../../components/DropDown/DropDown";
import { ValidatedTextInput } from "../../../../components/ValidatedInput/ValidatedTextInput";
import { countryCodeDropdown } from "../../util/RegisterModalUtils";
import { ValidateName, validatePhone } from "../../../../services/Validators";
import { StyledNextButton } from "../RegisterNextButton/RegisterNextButton";
import { updateUserPhone, updateRegister } from "../../../../redux/Slices/RegisterSlice";
import './RegisterFormFour.css'

export const RegisterFormFour:React.FC = () => {

    const state = useSelector((state:RootState) => state.reggister);

    const [phoneCode, setPhoneCode] = useState<string>("+1");
    const [phoneNumber, setPhoneNumber] = useState<string>("");
    const [validNumber, setValidNumber] = useState<boolean>(true);

    const dispatch:AppDispatch = useDispatch();



    const changeCode = (e:React.ChangeEvent<HTMLSelectElement>) => {
        setPhoneCode(e.target.value.split(" ")[0]);
    }

    const changePhoneNumber = (e:React.ChangeEvent<HTMLInputElement>) =>{
        setPhoneNumber(e.target.value);
        dispatch(updateRegister({
            name: "phoneNumber",
            value: e.target.value
        }))
    }


    const sendPhoneNumber = () =>{
        dispatch(updateUserPhone({
            username: state.username,
            phone: phoneNumber
        }));
    }


    useEffect(()=>{
        console.log(phoneCode, phoneNumber);
        if(phoneNumber){
            setValidNumber(validatePhone(phoneNumber));
        }
        console.log(state);
    }, [phoneCode, phoneNumber])

    return(
        <div className="reg-step-four-container">
            <div className="reg-step-four-content">
                <h1>Add a phone number</h1>
                <p className="reg-step-four-subhead">Enter the phone number you will like to associate with the organization account. You wont get a verification code sent here.</p>

                <div className="reg-step-four-inputs">
                    <DropDown 
                        content={countryCodeDropdown}
                        change={changeCode}
                        label={"Country code"}
                        defaultValue={"United States +1"}
                    />
                    <ValidatedTextInput 
                        valid={true}
                        name={"phoneNumber"}
                        label={"Your Phone Number "}
                        changeValue={changePhoneNumber}
                    />
                    {validNumber ? <></> : <p className="reg-step-four-invalid">Please enter a valid 10 digit number</p>}
                </div>

                <div className="reg-step-four-check-group">
                    <p>Let people who have your phone number find and connect with you on twitter. <span className="reg-step-four-link">Leran more</span>.</p>
                    <CheckBox/>
                </div>


                <div className="reg-step-four-check-group">
                    <p>Let app use your phone number to personalize our services, including ads (if permitted by your Ads preferences). If you don't enable twitter will  still use your phone number for purposes including account security, spam, fraud, and abuse prevention. <span className="reg-step-four-link">See our Privacy Policy for more information</span> </p>
                    <CheckBox/>
                </div>
            </div>
            <StyledNextButton 
                    disabled={(phoneNumber && validNumber) ? false : true}
                    color={'black'}
                    active={(phoneNumber && validNumber) ? true : false}
                    onClick={sendPhoneNumber}>
                        Update Number
                </StyledNextButton>
        </div>
    )
}
