import React from "react"
import { useDispatch , useSelector} from "react-redux";
import { AppDispatch, RootState } from "../../../redux/Store";

import { RegisterFormOne } from "../components/RegisterFormOne/RegisterFormOne"
import { RegisterFormTwo } from "../components/RegisterFormTwo/RegisterFormTwo";
import { RegisterFormThree } from "../components/RegisterFormThree/RegisterFormThree";
import { RegisterFormFour } from "../components/RegisterFormFour/RegisterFormFour";

import data from '../../../data/codes.json'
import { RegisterFormFive } from "../components/RegisterFormFive/RegisterFormFive";
import { RegisterFormSix } from "../components/RegisterFormSix/RegisterFormSix";
import { StyledNextButton } from "../components/RegisterNextButton/RegisterNextButton";

export const determineModalContent = (step: number):JSX.Element => {
    switch (step) {
        case 1: return <RegisterFormOne/>;
        case 2: return <RegisterFormTwo/ >
        case 3: return <RegisterFormThree/>
        case 4: return <RegisterFormFour/>
        case 5: return <RegisterFormFive/>
        case 6: return <RegisterFormSix/>
        default: return <></>
    }

}

export const determineModalButton = (step:number):JSX.Element => {
    const dispatch:AppDispatch = useDispatch();
    const state = useSelector(( state:RootState) => state.reggister);

    switch(step){
        case 1: 
        let buttonActive:boolean = state.dobValid && state.emailValid && state.firstNameValid && state.lastNameValid;

        return <StyledNextButton 
        disabled={!buttonActive}
        color={"black"}
        active={buttonActive}
        onClick={() => console.log("hello")}>
          Next
         </StyledNextButton>
    }

    return <div></div>
}

export const countryCodeDropdown = ():JSX.Element[] => {
    let options = data.filter((country) => {
        if (country.code !== "US"){
             return country;
        }
    }).map((country) => {
        return <option value={`${country.dial_code} ${country.name}`}
        key={country.code}>
            {`${country.dial_code} ${country.name}`}
        </option>
    })

    options.unshift(
        <option value={"+1 United states"} key={"US"}>{"+1 United States"}</option>
    )

    return options;
}
