import React, {useState, useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { AppDispatch, RootState } from "../../../../redux/Store";

import { ValidatedTextInput } from "../../../../components/ValidatedInput/ValidatedTextInput";
import { StyledNextButton } from "../RegisterNextButton/RegisterNextButton";
import { resendEmail, sendVerification, updateRegister } from "../../../../redux/Slices/RegisterSlice";
import './RegisterFormFive.css'



export const RegisterFormFive:React.FC = () => {
    const state = useSelector((state:RootState) => state.reggister);
    const dispatch:AppDispatch = useDispatch();

    const [code, setCode] = useState<string>(" ");


    const handleChange = (e:React.ChangeEvent<HTMLInputElement>) => {
        setCode(e.target.value);
        dispatch(updateRegister({
            name: "code",
             value: e.target.value
        }));
    } 


    //ressend the verification code
    const resendCode = () => {
        dispatch(
            resendEmail(state.username)
        ) 
    };


    //send Verification to the backend to verify
    const verify = () => {
        dispatch(
            sendVerification({
                username: state.username,
                code,
            })
        );
    }



    return (
        <div className="reg-step-five-container">
            <div className="reg-step-five-content">
                <h1>We sent you a code</h1>
                <p>Enter it below to verify {state.email}</p>
                <ValidatedTextInput 
                    valid={true}
                    name={"code"}
                    label={"Verification Code"}
                    changeValue={handleChange}/>
                    <p className="reg-step-five-message" onClick={resendCode}>Didn't receive a email?</p>

            </div>
        </div>
    )
}
