import React, {useState, useEffect} from "react";
import { useDispatch, useSelector} from "react-redux";

import { ValidatedDateSelector } from "../../../../components/ValidatedInput/ValidatedDateSelector";
import { getMonths, getDays, getYears } from "../../../../utils/DateUtils";
import { AppDispatch, RootState } from "../../../../redux/Store";
import { updateRegister } from "../../../../redux/Slices/RegisterSlice";
import { validateDob } from "../../../../services/Validators";

export const RegisterDateInput:React.FC = () =>{

    const state = useSelector((state:RootState) => state.reggister);
    const dispatch:AppDispatch = useDispatch();
    const [valid, setValid] = useState(true);

const updateState = (name:string, value:string|number|boolean ):void =>{
    dispatch (updateRegister({
        name,
        value
    }));
}
useEffect(() => {
    const { day, month, year } = state.dob;
    let isValid = false;

    if (day && month && year) {
      isValid = validateDob({ month, day, year });
    }

    dispatch(updateRegister({ name: 'dobValid', value: isValid }));
  }, [state.dob, dispatch]);



    return (
        <div className="register-date">
             <ValidatedDateSelector
             style={"validated-month"}
             valid = {valid}
             name={"Month"}
             dropDown={getMonths}
             dispatcher={updateState}
             />

            <ValidatedDateSelector
             style={"validated-day"}
             valid = {valid}
             name={"Day"}
             dropDown={getDays}
             dispatcher={updateState}
             />

            <ValidatedDateSelector
             style={"validated-year"}
             valid = {valid}
             name={"Year"}
             dropDown={getYears}
             dispatcher={updateState}
             />
        </div>
    );
}
