import React, {useState, useEffect} from 'react'

import { ValidateName } from '../../../services/Validators';
import './RegisterFormOne.css';
import { ValidatedInput } from '../../../components/ValidatedInput/ValidatedInput';
import { RegisterDateInput } from '../components/RegisterDateInput/RegisterDateInput';
import { RegisterNameInput } from '../components/RegisterNameInput/RegisterNameInput';
import { RegisterEmailInput } from '../components/RegisterEmailInput/RegisterEmailInputs';
import { StyledInputBox } from '../../../components/ValidatedInput/StyledInput';


//props
interface FormOneState {
    firstName: string;
    lastName: string;
    email: string;
    dateOfBirth: string;
}

// components
export const RegisterFormOne: React.FC = () => {

    const [stepOneState, setStepOneState] = useState<FormOneState>({
        firstName: " ", 
        lastName: " ",
        email: " ",
        dateOfBirth: " ",
    }) 

    const [buttonActive, setButtonActive] = useState<boolean>(false)

    // step one uodate user
    const updateUser= (e:React.ChangeEvent<HTMLInputElement>): void => {
        setStepOneState({...stepOneState, [e.target.name]: e.target.value })
    }



    // console display the update function
    useEffect(() => {
        console.log("State change: ", stepOneState);
    })



  return (
    <div className='reg-step-one-container'>
        <div className="reg-step-one-content">
         <RegisterNameInput/>
          <RegisterEmailInput/>
          <RegisterDateInput />
        </div>
        <StyledInputBox 
          disabled={!buttonActive}
          color={"black"}
          active={buttonActive}
          onClick={() => console.log("goto the next page")}>
            Next
        </StyledInputBox>
    </div>
  )
}
