import { createAsyncThunk, createSlice, PayloadAction } from "@reduxjs/toolkit";
import { Dob } from "../../utils/GlobalInterfaces";
import axios from "axios";


interface RegisterSliceState {
    loading: boolean;
    error: boolean;
    firstName: string;
    firstNameValid: boolean;
    lastName: string;
    lastNameValid:boolean;
    email:string;
    emailValid: boolean;
    dob:Dob;
    dobValid: boolean;
    step: number;
    username:string;
}


interface UpdatePayload{
    name: string;
    value: string | number | boolean;
}

interface RegisterUser {
    firstName:string;
    lastName:string;
    email:string;
    dob:string;
}
interface updatePhone{
    username:string;
    phone: string;
}

const initialState:RegisterSliceState = {
    loading:false,
    error: false,
    firstName: '',
    firstNameValid:  false,
    lastName: '',
    lastNameValid: false,
    email: '',
    emailValid: false,
    dob: {
        month : 0,
        day: 0,
        year: 0
    }, 
    dobValid: false,
    step: 4,
    username: " "
}


/* The action sends a POST request using `axios.post`, passing the `user` object as the payload.
 If the API request is successful, it returns the response data (`req.data`) as the result of
 the action. In case of failure, the action gracefully handles errors by rejecting with the
 error value using `thunkAPI.rejectWithValue(e)`.*/
export const registerUser = createAsyncThunk(
    'register/register',
    async(user:RegisterUser, thunkAPI) => {
        try{
            const req = await axios.post('http://localhost:8050/auth/register', user);
            return await req.data;
        }
        catch (e){
            return thunkAPI.rejectWithValue(e);
        }
    }
)


export const updateUserPhone = createAsyncThunk(
    'register/phone',
    async (body:updatePhone, thunkAPI) => {
        try{
            const req = await axios.put('http://localhost:8050/auth/update/phone', body);
            const email = await axios.post('http://localhost:8050/auth/email/code', {username:body.username});

        }catch(e){
             return thunkAPI.rejectWithValue(e);
        }
    }
)

export const resendEmail = createAsyncThunk(
    'register/resend',
    async(username:string, thunkAPI) => {
        try {
            const req = await axios.post('http://localhost:8050/auth/email/code', {username});
        } catch(e) {
            return thunkAPI.rejectWithValue(e);
        }
    }
)



// ---------------------------------------------------------------------
// RegisterSlice - Redux Slice for Register Data
// ---------------------------------------------------------------------
// This Redux slice named "register" manages the state for user registration data.
// It includes reducers to update specific fields in the registration state, such as
// date of birth (dob) fields, and individual fields for name, email, password, etc.
// Additionally, it provides reducers to increment and decrement the "step" value, which
// might be used to track the progress of a multi-step registration process or form.
//
// The "updateRegister" reducer handles both dob fields (month, day, year) and individual
// fields by dynamically updating the state. If the field is dob, it creates a new dob
// object with the updated value and merges it into the state. For other fields, it
// directly updates the field in the state.
//
// The "incrementStep" reducer increments the "step" value by 1, while the "decrementStep"
// reducer decrements the "step" value by 1, but only when the current step is between 2 and 5.
// If the current step is at the initial step, step 4, or step 6 or above, the decrement will
// not be allowed to avoid unwanted state changes.
// ---------------------------------------------------------------------
export const RegisterSlice = createSlice ({
    name: "register",
    initialState,
    reducers:{
        updateRegister(state, action:PayloadAction<UpdatePayload>){
            let {name, value} = action.payload;

            if(name === 'month' || name === 'day' || name === 'year'){
                let dob = state.dob;
                dob = {
                    ...dob,
                    [name]:value
                }
                state = {
                    ...state,
                    dob
                };
            }else{
                state = {
                    ...state,
                    [name]: value
                }
            }
            console.log('Updating the global register state:', state); 
            return state;
        },

        incrementStep(state){
            state.step++;
            return state;
        },
        decrementStep(state){
            if (state.step === 1 || state.step === 4|| state.step >= 6){
                return state;
            }else{
                state.step --;
                return state;
            }
        }
    },


    //Function to hold cases or things that happenes when request is made eg: pending..
    extraReducers: (builder) => {
        builder.addCase(registerUser.pending, (state, action) => {
          state.loading = true;
        });

        builder.addCase(updateUserPhone.pending, (state, action) => {
          state.loading = true;
        });

        builder.addCase(resendEmail.pending, (state, action) => {
          state.loading = true;
        });

        builder.addCase(registerUser.fulfilled, (state, action) => {
          let nextStep = state.step + 1;
          state.username = action.payload.username;
          state.loading = false;
          state.error = false;
          state.step = nextStep;
        });

        builder.addCase(updateUserPhone.fulfilled, (state, action) => {
          let nextStep = state.step + 1;
          state.loading = false;
          state.error = false;
          state.step = nextStep;
        });

        builder.addCase(resendEmail.fulfilled, (state, action) => {
          state.loading = false;
          state.error = false;
        });

        builder.addCase(registerUser.rejected, (state, action) => {
          state.error = true;
          state.loading = false;
        });

        builder.addCase(updateUserPhone.rejected, (state, action) => {
          state.loading = false;
          state.error = true;
        });

        builder.addCase(resendEmail.rejected, (state, action) => {
          state.loading = false;
          state.error = true;
        });
      }

});


export const {updateRegister, incrementStep, decrementStep } = RegisterSlice.actions;

export default RegisterSlice.reducer;
