import React, {useState, useEffect} from 'react'
import { useSelector } from 'react-redux';
import { RootState } from '../../../redux/Store';

import './RegisterFormOne.css';
import { RegisterDateInput } from '../components/RegisterDateInput/RegisterDateInput';
import { RegisterNameInput } from '../components/RegisterNameInput/RegisterNameInput';
import { RegisterEmailInput } from '../components/RegisterEmailInput/RegisterEmailInputs';
import { StyledNextButton } from '../components/RegisterNextButton/RegisterNextButton';

//props
interface FormOneState {
    firstName: string;
    lastName: string;
    email: string;
    dateOfBirth: string;
}

// components
export const RegisterFormOne: React.FC = () => {

     const registerState = useSelector((state:RootState) => state.reggister);

    const [buttonActive, setButtonActive] = useState<boolean>(false)



    // console display the update function
    useEffect(() => {
      if(registerState.dobValid && registerState.emailValid && registerState.firstName && registerState.lastNameValid){
          setButtonActive(true);
      }else{
        setButtonActive(false);
      }
    }, [registerState])



  return (
    <div className='reg-step-one-container'>
        <div className="reg-step-one-content">
         <RegisterNameInput/>
          <RegisterEmailInput/>
          <RegisterDateInput />
        </div>
        <StyledNextButton 
          disabled={!buttonActive}
          color={'black'}
          active={buttonActive}
          onClick={() => console.log("Go to the next page")}>
            Next
        </StyledNextButton>
    </div>
  )
}
