import React, { useState, useEffect}from 'react'

import { ValidatedInputState } from '../../utils/GlobalInterfaces';
import { StyledInputBox, StyledInputLabel } from './StyledInput'
import './ValidatedInput.css'

interface ValidatedUserInputProps  {
    name: string;
    label : string;
    errorMessage : string;
    validator(value:string) : boolean;
    changeValue(e:React.ChangeEvent<HTMLInputElement>): void;
    attribute?:Record<string, string | number | boolean>
}

export const ValidatedInput:React.FC<ValidatedUserInputProps> = ({
    name,
    label,
    errorMessage,
    validator,
    changeValue,
    attribute
}) => {

    const[validatedState, SetValidateState] = useState<ValidatedInputState>({
        active: false,
        valid: true,
        typeIn: false,
        labelActive: false,
        labelColor: "gray",
        value : ''
    });

    const focus = (e:React.FocusEvent<HTMLIFrameElement>) : void => {
        SetValidateState({
            ...validatedState, 
            active: !validatedState?.active
        });
    }


    //update value
    const updateValue = (e:React.ChangeEvent<HTMLInputElement>): void => {
        changeValue(e);
        SetValidateState({
          ...validatedState, 
            valid: validator(e.target.value),
            typeIn: true,
            value: e.target.value
        });
    }

    useEffect(() => {
        SetValidateState({
          ...validatedState, 
            valid: validator(name),
            typeIn: true
        });
    }, [name, validator]);

  return (
    <div className='validated-input'>
        <StyledInputBox active={false} valid={true}>
            <StyledInputLabel color={'gray'} active={false} valid={true}>{label} </StyledInputLabel>
            <input className='validated-inout-value'
                onFocus={() => {}}
                onBlur={() => {}}
                onChange={() => {}}
                {...attribute}
            />
        </StyledInputBox>

        <span>{errorMessage}</span>
    </div>
  )
}
