import { createAsyncThunk, createSlice, PayloadAction } from "@reduxjs/toolkit";
import { Dob } from "../../utils/GlobalInterfaces";
import axios, {AxiosError} from "axios";


interface RegisterSliceState {
    loading: boolean;
    error: boolean;
    firstName: string;
    firstNameValid: boolean;
    lastName: string;
    lastNameValid:boolean;
    email:string;
    emailValid: boolean;
    dob:Dob;
    dobValid: boolean;
    step: number;
    username:string;
}


interface UpdatePayload{
    name: string;
    value: string | number | boolean;
}

interface RegisterUser {
    firstName:string;
    lastName:string;
    email:string;
    dob:string;
}
interface updatePhone{
    username:string;
    phone: string;
}

const initialState:RegisterSliceState = {
    loading:false,
    error: false,
    firstName: '',
    firstNameValid:  false,
    lastName: '',
    lastNameValid: false,
    email: '',
    emailValid: false,
    dob: {
        month : 0,
        day: 0,
        year: 0
    }, 
    dobValid: false,
    step: 1,
    username: " "
}


/* The action sends a POST request using `axios.post`, passing the `user` object as the payload.
 If the API request is successful, it returns the response data (`req.data`) as the result of
 the action. In case of failure, the action gracefully handles errors by rejecting with the
 error value using `thunkAPI.rejectWithValue(e)`.*/
export const registerUser = createAsyncThunk(
    'register/register',
    async(user:RegisterUser, thunkAPI) => {
        try{
            const req = await axios.post('http://localhost:8050/auth/register', user);
            return await req.data;
        }
        catch (e){
            return thunkAPI.rejectWithValue(e);
        }
    }
)


export const updateUserPhone = createAsyncThunk(
    'register/phone',
    async (body:updatePhone, thunkAPI) => {
        try{
            const req = await axios.put('http://localhost:8050/auth/update/phone', body);
            const email = await axios.post('http://localhost:8050/auth/email/code', {username:body.username});

        }catch(e){
             //return thunkAPI.rejectWithValue(e);
             const error = e as AxiosError<any>;
            const errorPayload = {
            message: error.message, 
            status: error.response?.status, 
            data: error.response?.data, 
        };
            return thunkAPI.rejectWithValue(errorPayload);
        }
    }
)


