import React, {useState, useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { AppDispatch, RootState } from "../../../../redux/Store";
import { useNavigate } from "react-router-dom";
import { ValidatedTextInput } from "../../../../components/ValidatedInput/ValidatedTextInput";
import VisibilityOffOutlinedIcon  from "@mui/icons-material/VisibilityOffOutlined";
import  VisibilityOutlinedIcon from  "@mui/icons-material/VisibilityOutlined";
import { StyledNextButton } from "../RegisterNextButton/RegisterNextButton";
import { updateRegister, updateUserPassword } from "../../../../redux/Slices/RegisterSlice";
import './RegisterFormSix.css'

export const RegisterFormSix:React.FC = () => {
    const state = useSelector((state:RootState) => state.reggister);
    const dispatch:AppDispatch = useDispatch();

    const [ active, setActive] = useState<boolean> (false);
    const [password, setPassword] = useState<string>("");

    const navigate = useNavigate();

    const handleChange = (e:React.ChangeEvent<HTMLInputElement>) => {
        setPassword(e.target.value);
        dispatch(updateRegister({
            name: "passwordLength",
            value: e.target.value.length
        }));
    }

    //password toggle view and
    const toggleView = () => {
        setActive(!active);
    }

    useEffect(() =>{
        if(state.login){
            //store some user info into local storage, that way we can load the user into user slides 
            //when we hit the feed page
            navigate("/feed");
        }
    }, [state.login])

    return (
        <div className="reg-step-six-container">
            <div className="reg-step-six-content">
                <h1>You'll need a password</h1>
                <p>Make sure it's 8 characters or more</p>
                <div className="reg-step-six-password">
                    <ValidatedTextInput
                        valid={true}
                        label={"Password"}
                        name={"password"}
                        changeValue={handleChange}
                        attributes={{
                            minlenght: 8,
                            type: active ? "text" : "password"
                        }}/>
                        <div onClick={toggleView} className="reg-step-six-icon">
                            {active ? <VisibilityOffOutlinedIcon sx = {{fontSize: "24px"}}/> : <VisibilityOutlinedIcon sx={{fontSize: "24px"}}/>}
                        </div>
                </div>
            </div>
        </div>
    )

}
