import React from "react";
import { useSelector, useDispatch} from "react-redux";
import { RootState, AppDispatch } from "../../../../redux/Store";
 import { ValidatedDisplay } from "../../../../components/ValidatedInput/ValidatedDisplay";
 import { stringifyDate } from "../../../../utils/DateUtils";
import { StyledNextButton } from "../RegisterNextButton/RegisterNextButton";
import { registerUser } from "../../../../redux/Slices/RegisterSlice";
import './RegisterFormThree.css'

 export const RegisterFormThree:React.FC = () => {

    const state = useSelector((state:RootState) => state.reggister)
    const dispatch:AppDispatch = useDispatch();

    const submitUser = () => {
        // Perform validations on state.dob
        if (
            typeof state.dob.year !== 'number' ||
            typeof state.dob.month !== 'number' ||
            typeof state.dob.day !== 'number' ||
            isNaN(state.dob.year) ||
            isNaN(state.dob.month) ||
            isNaN(state.dob.day)
        ) {
            console.error('Invalid date of birth.');
            return; // Exit the function if the date of birth is invalid
        }

        // Format dob property in user object
        const user = {
            firstName: state.firstName,
            lastName: state.lastName,
            email: state.email,
            dob: `${state.dob.year}-${state.dob.month}-${state.dob.day}`
        };

        console.log('We are attempting to register the user.');
        dispatch(registerUser(user));
    };


    return(
        <div className="reg-step-three-container">
            <div className="reg-step-three-content">
                <h1 className="reg-step-three-header">Create your account</h1>
                <div className="reg-step-three-value">
                    <ValidatedDisplay label={"Name"} value={`${state.firstName} ${state.lastName}`}/>
                </div>
                <div className="reg-step-three-value"> 
                    <ValidatedDisplay label={"Email"} value={state.email}/>
                    {state.error ?  <p className="reg-step-three-error">The email is in use, please use a different email.</p> : <></>}
                </div>
                <div className="reg-step-three-value">
                    <ValidatedDisplay label={"Birth date"} value={stringifyDate(state.dob)}/>
                </div>
                    <p  className="reg-step-three-policy">
                        By signing up you agree <span className="reg-step-three-link">Terms of Services</span> and <span className="reg-step-three-link">Privacy Policy</span>, include <span className="reg-step-three-link">Cookies use</span>. The Organization may use your contact information, including your email address and phone number for purposes outlined in our Privacy Policy, like keeping your account secure and personalizing our services including ads. <span className="reg-step-three-link">Learn more</span>. Others will be able to find you by email orphone number, when provided unless you choose otherwise. <span className="reg-step-three-link">here</span>
                    </p>
            </div>
            <StyledNextButton onClick={submitUser} color={"black"} active={true}>
                Sign Up
            </StyledNextButton>
        </div>
    )
 }
