import React from "react";
import styled from "styled-components";
import { StyledNextButtonProps } from "../../../../utils/GlobalInterfaces";
import { AppDispatch, RootState } from "../../../../redux/Store";
import { useSelector, useDispatch } from "react-redux";

export const StyledNextButton = styled.button<StyledNextButtonProps>`
    width: 75%;
    height: 52px;
    font-size: 17px;
    color: white;
    background-color: ${(props) =>props.color === 'blue' ? props.theme.colors.blue : props.theme.colors.black};
    opacity: ${(props) => props.active ? 1.0 : .5};
    border-radius: 50px;
    border:none;
    cursor: ${(props) => props.active ? "pointer" : "auto"};
`;


interface RegisterNextButtonProps{
    step:number;
}

export const RegisterNextButton:React.FC<RegisterNextButtonProps> =({step}) =>{
    const state = useSelector((state:RootState) => state.reggister);
    const dispatch:AppDispatch = useDispatch();

    switch(step){
        case 1:
            let active = state.dobValid && state.emailValid && state.firstNameValid && state.lastNameValid;
            <StyledNextButton 
                disabled={!active}
                color={"black"}
                active={false}
                onClick={() => console.log("hello")}>
                {step} 
            </StyledNextButton>
        default:
            return <StyledNextButton 
                        disabled={true}
                        color={"black"}
                        active={false}
                        onClick={() => console.log("hello")}>
                        {step} 
                    </StyledNextButton>
    }
}
